package L2dec14;

public class pattern0 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int n=10;
		
			int row=1;
			while(row<=n) {
				//work
				int col=1;
				while(col<=n) {
					System.out.print("*");
					col++;
				}
				//prep
				System.out.println();
				row=row+1;
			}
	
	}

}
